import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import InventoryDashboard from './components/InventoryDashboard';
import ItemCreator from './components/ItemCreator';
import ItemRemover from './components/ItemRemover';
import ItemEditor from './components/ItemEditor';
import ItemList from './components/ItemList';
import ItemSearch from './components/ItemSearch';
import ItemSorter from './components/ItemSorter';
import LowStockNotifier from './components/LowStockNotifier';
import CategoryBasedItemList from './components/CategoryBasedItemList';
import './App.css';

const App = () => {
  const [inventory, setInventory] = useState([]);

  const addItemToInventory = (newItem) => setInventory([...inventory, newItem]);

  const removeItemFromInventory = (id) => {
    const itemExists = inventory.find((item) => item.id === id);
    if (itemExists) {
      setInventory(inventory.filter((item) => item.id !== id));
      return true;
    }
    return false;
  };

  const updateItemInInventory = (id, field, newValue) => {
    const itemIndex = inventory.findIndex((item) => item.id === id);
    if (itemIndex > -1) {
      const updatedInventory = [...inventory];
      updatedInventory[itemIndex][field] = parseFloat(newValue);
      setInventory(updatedInventory);
      return true;
    }
    return false;
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<InventoryDashboard />} />
          <Route path="/create-item" element={<ItemCreator addItemToInventory={addItemToInventory} inventory={inventory} />} />
          <Route path="/remove-item" element={<ItemRemover removeItemFromInventory={removeItemFromInventory} />} />
          <Route path="/edit-item" element={<ItemEditor updateItemInInventory={updateItemInInventory} />} />
          <Route path="/view-all-items" element={<ItemList inventory={inventory} />} />
          <Route path="/view-items-by-category" element={<CategoryBasedItemList inventory={inventory} />} />
          <Route path="/search-items" element={<ItemSearch inventory={inventory} />} />
          <Route path="/sort-items" element={<ItemSorter inventory={inventory} />} />
          <Route path="/low-stock-alerts" element={<LowStockNotifier inventory={inventory} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
